'  member function readPoint(pos in out pls_integer, dim in pls_integer) return sdo_geometry is ',
'    l_x number;',
'    l_y number;',
'    l_z number;',
'    l_m number;',
'    l_recshapetype pls_integer;',
'  begin',
'    -- read record header',
